import { Injectable } from '@angular/core';
import { Observable, BehaviorSubject, of, throwError } from 'rxjs';
import { catchError, map, tap } from 'rxjs/operators';
import * as signalR from '@microsoft/signalr';
import { AuthService } from './auth.service';

@Injectable({
    providedIn: 'root'
})
export class SignalRFactoryService {
    private hubConnections = new Map<string, BehaviorSubject<{
        connection: signalR.HubConnection;
        status: 'connected' | 'disconnected' | 'connecting' | 'error';
    }>>();

    constructor(private authService: AuthService) { }

    getHubConnection(hubUrl: string): Observable<signalR.HubConnection> {
        // Create a subject for this hub if it doesn't exist
        if (!this.hubConnections.has(hubUrl)) {
            const connectionSubject = new BehaviorSubject<{
                connection: signalR.HubConnection;
                status: 'connected' | 'disconnected' | 'connecting' | 'error';
            }>({
                connection: this.createHubConnection(hubUrl),
                status: 'disconnected'
            });

            this.hubConnections.set(hubUrl, connectionSubject);
            this.initializeConnection(hubUrl);
        }

        // Return the connection
        return this.hubConnections.get(hubUrl)!.pipe(
            map(info => {
                if (info.status === 'error') {
                    throw new Error(`Failed to connect to ${hubUrl}`);
                }
                return info.connection;
            })
        );
    }

    getConnectionStatus(hubUrl: string): Observable<string> {
        if (!this.hubConnections.has(hubUrl)) {
            return of('not_initialized');
        }

        return this.hubConnections.get(hubUrl)!.pipe(
            map(info => info.status)
        );
    }

    private createHubConnection(hubUrl: string): signalR.HubConnection {
        return new signalR.HubConnectionBuilder()
            .withUrl(hubUrl, {
                accessTokenFactory: () => '' // Will be set later
            })
            .withAutomaticReconnect()
            .build();
    }

    private initializeConnection(hubUrl: string): void {
        const subject = this.hubConnections.get(hubUrl)!;

        // Update status to connecting
        subject.next({
            ...subject.value,
            status: 'connecting'
        });

        // Get auth token and connect
        this.authService.getAccessToken().subscribe({
            next: token => {
                if (!token) {
                    subject.next({
                        ...subject.value,
                        status: 'error'
                    });
                    return;
                }

                // Set the token factory
                const connection = subject.value.connection;
                (connection as any).httpConnectionOptions = {
                    ...(connection as any).httpConnectionOptions,
                    accessTokenFactory: () => token
                };

                // Start the connection
                connection.start()
                    .then(() => {
                        console.log(`Connected to ${hubUrl}`);
                        subject.next({
                            connection,
                            status: 'connected'
                        });
                    })
                    .catch(err => {
                        console.error(`Error connecting to ${hubUrl}:`, err);
                        subject.next({
                            ...subject.value,
                            status: 'error'
                        });
                    });
            },
            error: err => {
                console.error(`Error getting auth token for ${hubUrl}:`, err);
                subject.next({
                    ...subject.value,
                    status: 'error'
                });
            }
        });
    }
}

import { Injectable, inject } from '@angular/core';
import * as signalR from '@microsoft/signalr';
import { BehaviorSubject } from 'rxjs';
import { AuthService } from './auth.service';

@Injectable({
  providedIn: 'root'
})
export class SignalRService {
  private hubConnection: signalR.HubConnection | null = null;
  public messages$ = new BehaviorSubject<string[]>([]);
  private readonly authService = inject(AuthService);

  constructor() {
    this.initConnection();
  }

  private initConnection() {
    this.authService.getAccessToken().subscribe(token => {
      if (token) {
        this.createConnection(token);
        this.start();
      } else {
        console.warn('No auth token available for SignalR connection');
      }
    });

    // Listen for auth state changes to recreate connection if needed
    this.authService.authState$.subscribe(state => {
      if (state.isAuthenticated && state.accessToken && (!this.hubConnection || this.hubConnection.state === signalR.HubConnectionState.Disconnected)) {
        this.createConnection(state.accessToken);
        this.start();
      }
    });
  }

  private createConnection(token: string) {
    // Close existing connection if open
    if (this.hubConnection) {
      this.hubConnection.off('ReceiveMessage');
      this.hubConnection.stop().catch(err => console.error('Error stopping connection:', err));
    }

    // Create new connection with auth token
    this.hubConnection = new signalR.HubConnectionBuilder()
      .withUrl('https://localhost:7048/hubs/notification', {
        accessTokenFactory: () => token
      })
      .withAutomaticReconnect()
      .build();

    // Set up message handler
    this.hubConnection.on('ReceiveMessage', (message: string) => {
      const currentMessages = this.messages$.value;
      this.messages$.next([...currentMessages, message]);
    });
  }

  private async start() {
    if (!this.hubConnection) {
      console.error('Cannot start SignalR: No connection created');
      return;
    }

    try {
      await this.hubConnection.start();
      console.log('SignalR Connected!');
    } catch (err) {
      console.error('SignalR Connection Error: ', err);
      setTimeout(() => this.start(), 5000);
    }
  }

  async sendMessage(message: string) {
    if (!this.hubConnection || this.hubConnection.state !== signalR.HubConnectionState.Connected) {
      console.error('Cannot send message: SignalR connection not established');
      return;
    }

    try {
      await this.hubConnection.invoke('SendMessage', message);
    } catch (err) {
      console.error('Error sending message: ', err);
    }
  }
}

