@use 'sass:map';
@use '../../../../styles';
@use '../../../../styles/variables' as vars;

.add-product-container {
    max-width: 1200px;
    margin: 0 auto;

    .header {
        margin-bottom: 2rem;

        h1 {
            margin-bottom: 0.5rem;
            color: var(--text-primary);
        }

        p {
            color: var(--text-secondary);
        }
    }
}

.form-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
    margin-bottom: 2rem;
}

.form-section {
    background-color: var(--bg-secondary);
    padding: 1.5rem;
    border-radius: 8px;
    border: 1px solid var(--border);

    h2 {
        font-size: 1.25rem;
        margin-bottom: 1.5rem;
        color: var(--text-primary);
    }
}

.form-group {
    margin-bottom: 1.5rem;

    label {
        display: block;
        margin-bottom: 0.5rem;
        color: var(--text-secondary);
    }

    .form-control {
        width: 100%;
        padding: 0.75rem;
        border: 1px solid var(--border);
        border-radius: 4px;
        background-color: var(--bg-primary);
        color: var(--text-primary);

        &:focus {
            border-color: var(--primary);
            outline: none;
        }
    }
}

.file-container {
    .file-box {
        border: 2px dashed var(--primary);
        border-radius: 8px;
        padding: 1rem;
        text-align: center;
        cursor: pointer;
        background-color: var(--bg-secondary);
        transition: all 0.3s ease;

        &:hover {
            background-color: rgba(var(--primary-rgb), 0.1);
        }

        i {
            color: var(--primary);
        }

        p {
            color: var(--text-primary);
        }
    }
}

.image-preview-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(100px, 1fr));
    gap: 1rem;
    margin-top: 1rem;

    .image-preview {
        position: relative;
        aspect-ratio: 1;

        img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            border-radius: 4px;
        }

        .remove-image {
            position: absolute;
            top: -0.5rem;
            right: -0.5rem;
            width: 1.5rem;
            height: 1.5rem;
            border-radius: 50%;
            background-color: var(--bg-dark);
            border: none;
            color: var(--text-primary);
            display: flex;
            align-items: center;
            justify-content: center;
            cursor: pointer;

            &:hover {
                background-color: var(--primary);
            }
        }
    }
}

.form-actions {
    display: flex;
    justify-content: flex-end;
    gap: 1rem;

    .btn {
        padding: 0.75rem 1.5rem;
        border-radius: 4px;
        font-weight: 500;

        &.btn-primary {
            background-color: var(--primary);
            border: none;
            color: var(--text-primary);

            &:hover {
                filter: brightness(90%);
            }
        }

        &.btn-outline-secondary {
            background: none;
            border: 1px solid var(--border);
            color: var(--text-secondary);

            &:hover {
                background-color: var(--bg-secondary);
            }
        }
    }
}

@media (max-width: get-breakpoint('md')) {
    .form-grid {
        grid-template-columns: 1fr;
        gap: 1rem;
    }

    .add-product-container {
        padding: 1rem;
    }

    .form-section {
        padding: 1rem;
    }

    .form-actions {
        flex-direction: column;
        align-items: stretch;

        .btn {
            width: 100%;
            margin-bottom: 0.5rem;
        }
    }
}

.scanner-section {
    margin-bottom: 2rem;
    padding: 1rem;
    background-color: #f9f9f9;
    border-radius: 8px;
    border: 1px solid #e0e0e0;
}

.scanner-section h3 {
    margin-top: 0;
    margin-bottom: 1rem;
    color: #333;
    font-size: 1.2rem;
}

