    justify-content: space-between;
    align-items: center;
    border-top: 1px solid var(--border);
    transform: translateY(100%);
    transition: transform 0.3s ease;
    z-index: 1000;

    &.visible {
        transform: translateY(0);
    }
}

.selected-info {
    display: flex;
    align-items: center;
    gap: 1rem;

    .count {
        font-weight: 500;
        color: var(--text-primary);
    }
}

.actions {
    display: flex;
    gap: 1rem;
}

