import { Component, ViewChild, ElementRef } from '@angular/core';
import { FieldType, FieldTypeConfig, FormlyModule } from '@ngx-formly/core';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule } from '@angular/forms';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';

@Component({
  selector: 'formly-field-color-picker',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    MatFormFieldModule,
    MatInputModule,
    MatButtonModule,
    MatIconModule,
    FormlyModule
  ],
  template: `
    <mat-form-field class="w-full">
      <mat-label>{{ props.label }}</mat-label>
      <div class="flex items-center">
        <input
          matInput
          [formControl]="formControl"
          [formlyAttributes]="field"
          [placeholder]="props.placeholder || ''"
          class="flex-grow"
        />
      <input
      type="color"
      #colorPicker
      (input)="updateColor($event)"
      class="w-full h-10 border-none rounded-md cursor-pointer"
      />
      </div>
      <mat-hint *ngIf="props.description">{{ props.description }}</mat-hint>
      <mat-error *ngIf="formControl.invalid">{{ getErrorMessage() }}</mat-error>
    </mat-form-field>
  `
})
export class FormlyColorPickerTypeComponent extends FieldType<FieldTypeConfig> {
  @ViewChild('colorPicker') colorPickerRef!: ElementRef<HTMLInputElement>;



  updateColor(event: Event): void {
    const input = event.target as HTMLInputElement;
    if (input && input.value) {
      this.formControl.setValue(input.value);
    }
  }

  getErrorMessage(): string {
    const errors = this.formControl.errors;
    if (!errors) return '';

    if (errors['required']) {
      return 'This field is required';
    }

    if (errors['pattern']) {
      return 'Invalid color format. Use hex format (#RRGGBB)';
    }

    return Object.keys(errors).map(key => errors[key]).join(', ');
  }
}

import { Component } from '@angular/core';
import { FieldType, FieldTypeConfig } from '@ngx-formly/core';

@Component({
  selector: 'formly-field-file',
  standalone: true,
  templateUrl: './image-upload.type.html',
  styleUrls: ['./image-upload.type.scss']
})
export class FormlyImageUploadTypeComponent extends FieldType<FieldTypeConfig> {
  previewUrls: string[] = [];
  private files: File[] = [];  // Store actual File objects

  onFileInputClick(): void {
    const fileInput = document.createElement('input');
    fileInput.type = 'file';
    fileInput.multiple = true;
    fileInput.accept = 'image/*';
    fileInput.onchange = (e: Event) => {
      const input = e.target as HTMLInputElement;
      if (input.files) {
        this.handleFiles(Array.from(input.files));
      }
    };
    fileInput.click();
  }

  onDragOver(event: DragEvent): void {
    event.preventDefault();
    event.stopPropagation();
  }

  onDrop(event: DragEvent): void {
    event.preventDefault();
    event.stopPropagation();
    const files = event.dataTransfer?.files;
    if (files) {
      this.handleFiles(Array.from(files));
    }
  }

  handleFiles(newFiles: File[]): void {
    if (!this.formControl) {
      console.error('Form control is not initialized');
      return;
    }

    const imageFiles = newFiles.filter(file =>
      file.type.startsWith('image/') &&
      file.size <= 5 * 1024 * 1024
    );

    if (this.files.length + imageFiles.length > 5) {
      this.formControl.setErrors({ maxFiles: true });
      return;
    }

    imageFiles.forEach(file => {
      // Store the actual File object
      this.files.push(file);

      // Create preview URL
      const reader = new FileReader();
      reader.onload = (e: ProgressEvent<FileReader>) => {
        const result = e.target?.result as string;
        if (result) {
          this.previewUrls = [...this.previewUrls, result];
        }
      };
      reader.readAsDataURL(file);
    });

    // Set the actual File objects as the form value
    this.formControl.setValue(this.files);
    this.formControl.markAsTouched();
  }

  removeImage(previewUrl: string): void {
    const index = this.previewUrls.indexOf(previewUrl);
    if (index !== -1) {
      this.previewUrls.splice(index, 1);
      this.files.splice(index, 1);
      this.formControl?.setValue(this.files);
      this.formControl?.markAsTouched();
    }
  }
}

