.image-wrapper {
  position: relative;
  aspect-ratio: 1;
}

.image-wrapper img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.primary-indicator {
  position: absolute;
  top: 0.5rem;
  left: 0.5rem;
  background-color: #3f51b5;
  color: white;
  font-size: 0.75rem;
  padding: 0.25rem 0.5rem;
  border-radius: 4px;
  display: flex;
  align-items: center;
  gap: 0.25rem;
}

.primary-indicator mat-icon {
  font-size: 16px;
  width: 16px;
  height: 16px;
}

.image-actions {
  position: absolute;
  top: 0.5rem;
  right: 0.5rem;
  opacity: 0;
  transition: opacity 0.2s ease;
}

.image-wrapper:hover .image-actions {
  opacity: 1;
}

.image-info {
  padding: 0.5rem;
  display: flex;
  flex-direction: column;
  font-size: 0.75rem;
}

.file-name {
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.file-size {
  color: #666;
}

.help-text {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 0.875rem;
  color: #666;
  margin-top: 0.5rem;
}

.cdk-drag-preview {
  box-shadow: 0 5px 15px rgba(0,0,0,0.2);
}

.cdk-drag-placeholder {
  opacity: 0.3;
}

.cdk-drag-animating {
  transition: transform 250ms cubic-bezier(0, 0, 0.2, 1);
}

